	         function rotateY(m, angle) {
	            var c = Math.cos(angle);
	            var s = Math.sin(angle);
	            var mv0 = m[0], mv4 = m[4], mv8 = m[8];
					
	            m[0] = c*m[0]+s*m[2];
	            m[4] = c*m[4]+s*m[6];
	            m[8] = c*m[8]+s*m[10];

	            m[2] = c*m[2]-s*mv0;
	            m[6] = c*m[6]-s*mv4;
	            m[10] = c*m[10]-s*mv8;
	         }

